import com.lowagie.text.*;
import com.lowagie.text.pdf.*;
import com.lowagie.text.pdf.PdfPageEventHelper;
import com.lowagie.text.pdf.draw.LineSeparator;
import java.awt.Color;
import groovy.json.JsonSlurper;

boolean asrtStatus = true
Date date = new Date()
String currentTime = date.format("MMM dd, yyyy hh:mm:ss a")
String datePart = date.format("ddMMyyyy")
String timePart = date.format("HHmmss")

final Font subHeadFont = FontFactory.getFont(FontFactory.COURIER_BOLD, 13, Font.BOLD);
final Font headFont = FontFactory.getFont(FontFactory.COURIER, 18, Font.BOLD);
final Font textFont = FontFactory.getFont(FontFactory.COURIER, 11);
final Font thFont = FontFactory.getFont(FontFactory.COURIER, 11, Font.BOLD);

// Get a project property
def projectName = runner.testSuite.project.name
def testSuiteName = runner.testSuite.name
int tcCount = runner.testSuite.getTestCaseCount()

HeaderFooterPageEvent event = new HeaderFooterPageEvent(testSuiteName);
Document document = new Document();
document.setMargins(36, 36, 135, 50);
File jsonCfgFile
def jsonCfgData 

try {
    jsonCfgFile = new File("C:\\SoapUI\\logs\\"+testSuiteName+"\\temp\\reportConfig.json");
    jsonCfgData = new JsonSlurper().parse(jsonCfgFile)
} catch(Exception e){
    log.info "DataDrivenTestSuite was not run prior to running genearting the report";
    return;
}

def failAsrtCount=0,failTSCount=0,failTCCount=0,TSCount=0,AsrtCount=0; 
def endpoint,dataSources,startTime

if (jsonCfgFile.exists()) {
	TSCount 	  = jsonCfgData.TSCount;
	AsrtCount 	  = jsonCfgData.AsrtCount;
	failAsrtCount = jsonCfgData.failAsrtCount;
	failTSCount   = jsonCfgData.failTSCount;
	failTCCount	  = jsonCfgData.failTCCount;
    endpoint      = jsonCfgData.endpoint;
    dataSources   = jsonCfgData.dataSources;
    startTime     = jsonCfgData.startTime
}    

try{
    PdfWriter.getInstance(document, new FileOutputStream("C:\\SoapUI\\logs\\" + testSuiteName + "\\TestReport_"+ datePart+ "_" + timePart +".pdf")).setPageEvent(event);

    document.open();

    Paragraph p = new Paragraph("TestSuite Metrics",headFont);
    p.setSpacingBefore(10f);
    p.setSpacingAfter(20f);
    document.add(p);

    document.add(new Paragraph("Overview",subHeadFont));
    document.add(new LineSeparator(0.5f, 100, null, 0, -5));

    PdfPTable table = new PdfPTable(2);
    table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
    table.getDefaultCell().setBorderColor(Color.WHITE);
    table.setHorizontalAlignment(Element.ALIGN_LEFT);
    table.getDefaultCell().setPadding(5);
    table.setSpacingBefore(10f);
    table.setSpacingAfter(20f);
    table.setWidthPercentage(100);
    int[] relWidth = [1, 2];
    table.setWidths(relWidth);
    table.addCell(new Phrase("Project Name",textFont));
    table.addCell(new Phrase(projectName,textFont));
    table.addCell(new Phrase("TestSuite Name",textFont));
    table.addCell(new Phrase(testSuiteName,textFont));
    document.add(table);
    table.flushContent();

    document.add(new Paragraph("TestCase Summary",subHeadFont));
    document.add(new LineSeparator(0.5f, 100, null, 0, -5));
    p = new Paragraph();
    p.setSpacingBefore(10f);
    p.setSpacingAfter(20f);
    List tcList = new List();
    tcList.setListSymbol("\u2022\u0020\u0020");
    tcList.setIndentationLeft(5f);

    for (int i=0;i < tcCount ;i++){

        def currTestCase = runner.testSuite.getTestCaseAt(i)
        def testSteps=currTestCase.getTestStepList()
        tcList.add(new ListItem(currTestCase.name,textFont))

        asrtStatus = true

        if ((testSteps.size() < 2) || (testSteps.get(0).name != "DataSource&Loop")){
            TSCount = TSCount + testSteps.size()
            AsrtCount = AsrtCount + testSteps.get(0).getAssertionCount()
            for(int k=0;k<testSteps.get(0).getAssertionCount();k++){
                def iAssertionStatus = testSteps.get(0).getAssertionAt(k).getStatus().toString()
                if(iAssertionStatus=="FAILED"){
                    asrtStatus = false
                    failAsrtCount++
                }                   
            }

            if(asrtStatus == false){
                failTSCount++
                failTCCount++
            }
        }
    }
    p.add(tcList);    
    document.add(p);

    document.add(new Paragraph("Base Metrics",subHeadFont));
    document.add(new LineSeparator(0.5f, 100, null, 0, -5));
    PdfPTable table1 = new PdfPTable(table);

    if (jsonCfgData == null){return;}

    table1.addCell(new Phrase("Number of test cases",textFont));
    table1.addCell(new Phrase(tcCount.toString(),textFont));
    table1.addCell(new Phrase("Number of test steps",textFont));
    table1.addCell(new Phrase(TSCount.toString(),textFont));
    table1.addCell(new Phrase("Number of assertions",textFont));
    table1.addCell(new Phrase(AsrtCount.toString(),textFont));
    document.add(table1);
    table1.flushContent();
    document.newPage();

    p = new Paragraph("Result Metrics & Parameters",headFont);
    p.setSpacingBefore(10f);
    p.setSpacingAfter(20f);
    document.add(p);

    document.add(new Paragraph("Result Metrics",subHeadFont));
    document.add(new LineSeparator(0.5f, 100, null, 0, -5));

    PdfPTable table2 = new PdfPTable(table);

    table2.addCell(new Phrase("Start Time",textFont));
    table2.addCell(new Phrase(startTime.toString(),textFont));
    table2.addCell(new Phrase("End Time",textFont));
    table2.addCell(new Phrase(currentTime.toString(),textFont));
    table2.addCell(new Phrase("Time Taken",textFont));
    table2.addCell(new Phrase(runner.timeTaken.toString() + " ms",textFont));

    table2.addCell(new Phrase("Test Case Count",textFont));
    table2.addCell(new Phrase(tcCount.toString(),textFont));
    table2.addCell(new Phrase("Failed Test Case Count",textFont));
    table2.addCell(new Phrase(failTCCount.toString(),textFont));
    table2.addCell(new Phrase("Test Step Count",textFont));
    table2.addCell(new Phrase(TSCount.toString(),textFont));
    table2.addCell(new Phrase("Failed Test Step Count",textFont));
    table2.addCell(new Phrase(failTSCount.toString(),textFont));
    table2.addCell(new Phrase("Assetion Count",textFont));
    table2.addCell(new Phrase(AsrtCount.toString(),textFont));
    table2.addCell(new Phrase("Failed Assertion Count",textFont));
    table2.addCell(new Phrase(failAsrtCount.toString(),textFont));
    document.add(table2);
    table2.flushContent();

    document.add(new Paragraph("Parameters",subHeadFont));
    document.add(new LineSeparator(0.5f, 100, null, 0, -5));

    PdfPTable table3 = new PdfPTable(table);

    table3.addCell(new Phrase("EndPoint",textFont));
    table3.addCell(new Phrase(endpoint.toString(),textFont));
    table3.addCell(new Phrase("DataSources",textFont));
    table3.addCell(new Phrase(dataSources.toString(),textFont));

    document.add(table3);
    table3.flushContent();
    document.newPage();

    p = new Paragraph("Test Suite Results",headFont);
    p.setSpacingBefore(10f);
    p.setSpacingAfter(20f);
    document.add(p);

    PdfPCell cell
    def index =0
    def tcItem
    def cfgFieldName

    for (i=0;i < tcCount ;i++){
        def currTestCase = runner.testSuite.getTestCaseAt(i)
        def testSteps=currTestCase.getTestStepList()

        table = new PdfPTable(3);
        table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
        table.getDefaultCell().setBorderColor(Color.WHITE);
        table.setHorizontalAlignment(Element.ALIGN_LEFT);
        table.getDefaultCell().setPadding(5);
        table.setSpacingBefore(20f);
        table.setSpacingAfter(20f);
        table.setWidthPercentage(100);
        table.setSplitRows(true);
        relWidth = [2,1,1];
        table.setWidths(relWidth);

        document.add(new Paragraph(currTestCase.name,subHeadFont));
        document.add(new LineSeparator(0.5f, 100, null, 0, -5));

        asrtStatus = true

        if ((testSteps.size() < 2) || (testSteps.get(0).name != "DataSource&Loop")){

            for(int k=0;k < testSteps.get(0).getAssertionCount();k++){
                def iAssertionStatus = testSteps.get(0).getAssertionAt(k).getStatus().toString()
                if(iAssertionStatus=="FAILED")
                    asrtStatus = false               
            }

            cell = new PdfPCell(new Phrase("Test Description",thFont));
            cell.setBorder(PdfPCell.NO_BORDER);
            cell.setCellEvent(new DottedCell(PdfPCell.BOTTOM));
            table.addCell(cell);
            cell.setPhrase(new Phrase("Run Status",thFont));
            table.addCell(cell);
            cell.setPhrase(new Phrase("Response Time(ms)",thFont));
            table.addCell(cell);
            table.setHeaderRows(1);       

            table.addCell(new Phrase(currTestCase.name,textFont));

            if(asrtStatus == false){
                cell = new PdfPCell(new Phrase("FAILED",textFont));
                cell.setBorder(PdfPCell.NO_BORDER);
                cell.setCellEvent(new CellBackground("RED"));
            } else{
                cell = new PdfPCell(new Phrase("PASSED",textFont));
                cell.setBorder(PdfPCell.NO_BORDER);
                cell.setCellEvent(new CellBackground("GREEN"));
            }
            table.addCell(cell); 
            table.addCell(new Phrase(testSteps.get(0).testRequest.messageExchange.getResponse().getTimeTaken().toString(),textFont));
            document.add(table);
            table.flushContent();

        } else {

            cell = new PdfPCell(new Phrase("Test Description",thFont));
            cell.setBorder(PdfPCell.NO_BORDER);
            cell.setCellEvent(new DottedCell(PdfPCell.BOTTOM));
            table.addCell(cell);
            cell.setPhrase(new Phrase("Run Status",thFont));
            table.addCell(cell);
            cell.setPhrase(new Phrase("Response Time(ms)",thFont));
            table.addCell(cell);
            table.setHeaderRows(1);

            tcItem = jsonCfgData.tcItems[index]

            for(int k=0;k<tcItem.DStcCount;k++){
                cfgFieldName = "TCDesc_"+ k
                table.addCell(new Phrase(tcItem."$cfgFieldName",textFont));
                cfgFieldName = "TCStatus_"+ k
                cell = new PdfPCell(new Phrase(tcItem."$cfgFieldName",textFont));
                cell.setBorder(PdfPCell.NO_BORDER);
                if(tcItem."$cfgFieldName"=="FAILED")
                    cell.setCellEvent(new CellBackground("RED"));
                else
                    cell.setCellEvent(new CellBackground("GREEN"));
                table.addCell(cell);    
                cfgFieldName = "responseTime_"+ k
                table.addCell(new Phrase(tcItem."$cfgFieldName".toString(),textFont));
            }
            index++
            document.add(table);
        }    
    }
}catch(Exception e){
    throw e;
}finally{
    document.close();
}   


if (jsonCfgFile.exists()){
    jsonCfgFile.delete()
