class DottedCell implements PdfPCellEvent {
    private int border = 0;
    public DottedCell(int border) {
        this.border = border;
    }
    public void cellLayout(PdfPCell cell, Rectangle position,
        PdfContentByte[] canvases) {
        PdfContentByte canvas = canvases[PdfPTable.LINECANVAS];
        canvas.saveState();
        //canvas.setLineDash(0, 4, 2);
        canvas.moveTo(position.getRight(), position.getBottom());
        canvas.lineTo(position.getLeft(), position.getBottom());
        canvas.stroke();
        canvas.restoreState();
    }
}

class CellBackground implements PdfPCellEvent {
    String color;
    public CellBackground(String color){
        this.color = color;
    }

    public void cellLayout(PdfPCell cell, Rectangle rect,
            PdfContentByte[] canvas) {
        PdfContentByte cb = canvas[PdfPTable.BACKGROUNDCANVAS];
        cb.rectangle(rect.left, rect.bottom,(float)(rect.width - 7f),(float) (rect.height - 3f));
        if (this.color == "RED")    
            cb.setCMYKColorFill(0,100,100,30);
        else
            cb.setCMYKColorFill(100,0,100,30);    
        cb.fill();
    }
}


public class HeaderFooterPageEvent extends PdfPageEventHelper {

    final Font textFont = FontFactory.getFont(FontFactory.COURIER, 10);
    final Font titleFont = FontFactory.getFont(FontFactory.COURIER_BOLD, 22, Font.BOLD);
    final Font nameFont = FontFactory.getFont(FontFactory.COURIER_BOLD, 16, Font.BOLD);
    String testSuiteName

    public HeaderFooterPageEvent(String testSuiteName){
        this.testSuiteName = testSuiteName
    }

    public void onStartPage(PdfWriter writer, Document document) {

        //String imgURL = "http://www.nationwideintranet.co.uk/SiteCollectionImages/NBS-Village-Icon-51x52.png";
        PdfContentByte cb = writer.getDirectContent();
        Image image;
        try {
        //  image = Image.getInstance(new URL(imgURL));
            image = Image.getInstance("//dvnslcoew01/NSL_CoE_Public/Soap UI NG Pro/logo.png");
            image.setAlignment(Element.ALIGN_CENTER);
            image.setAbsolutePosition(480, 800);
            image.scalePercent(28f, 28f);

            cb.addImage(image, true);

        } catch (IOException | DocumentException e) {
            ColumnText.showTextAligned(cb, Element.ALIGN_CENTER, new Phrase("SI_SQUAD - ESB_UnitTest_Report",textFont), 125, 810, 0);
        }


        ColumnText.showTextAligned(cb, Element.ALIGN_LEFT,
                    new Phrase("TestSuite Results Report",titleFont),
                     document.left(),(float)(document.top() + 65f), 0);

        ColumnText ct = new ColumnText(cb);
        ct.setSimpleColumn(new Phrase(testSuiteName,nameFont),document.left(),(float)(document.top() + 10f),document.right(),(float)(document.top() + 55f),15,Element.ALIGN_LEFT);
        ct.go();             

        new LineSeparator().drawLine(cb,(float)(document.left()),document.right(),(float)(document.top() + 10f)); 

    }

    public void onEndPage(PdfWriter writer, Document document) {
        Date date = new Date()
        String currentTime = date.format("MMM dd, yyyy hh:mm:ss a")
        ColumnText.showTextAligned(writer.getDirectContent(), Element.ALIGN_CENTER, new Phrase("Custom SOAPUI Report - " + currentTime,textFont), 180, 30, 0);
        ColumnText.showTextAligned(writer.getDirectContent(), Element.ALIGN_CENTER, new Phrase("Page " + document.getPageNumber(),textFont), 550, 30, 0);
    }

}

con:eventHandlers.con:script:
// Sample event script to add custom HTTP header to all outgoing REST, SOAP and HTTP(S) calls
// This code is often used for adding custom authentication to SoapUI Pro tests

